package com.oracle.hkeaa.custom.captcha.servlet;import nl.captcha.Captcha;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;public class RefreshServlet extends HttpServlet {    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) {        HttpSession session = req.getSession();        session.removeAttribute(Captcha.NAME);    }}